$(document).ready(function(){$(".tooltip").tooltipster({contentAsHTML:true,animation:"fade",delay:100,functionInit:function(a,b){tipParts=b.split("::");if(tipParts[0]===""){return b}tipTitle=tipParts[0];tipData=tipParts[1];formatted='<span class="tool-title">'+tipTitle+"</span><br/>"+tipData;return formatted}})});